<script>
    import Header from './UI/Header.svelte';
    import MeetupGrid from './Meetups/MeetupGrid.svelte';
    import TextInput from './UI/TextInput.svelte';
    import Button from './UI/Button.svelte';

    let title = '';
    let subtitle = '';
    let address = '';
    let email = '';
    let imageUrl = '';
    let description = '';

    let meetups = [
        {
            id: 'm1',
            title: 'Coding bootcamp',
            subtitle: 'Learn to code in 2 hours',
            description: 'Let\'s learn to code together with experienced coders',
            imageUrl: 'data:image/jpeg;base64,',
            address: '27 Nerd Road, 12345',
            contactEmail: 'nerd@nerd.com'
        },
        {
            id: 'm2',
            title: 'Swimming afternoon',
            subtitle: 'Let\'s learn to swim',
            description: 'Swimming rounds is great for your health',
            imageUrl: 'data:image/jpeg;base64,',
            address: '27 Nerd Road, 12345',
            contactEmail: 'nerd@nerd.com'
        },
    ]

    function addMeetup() {
        let newMeetup = {
            id: Math.random().toString(),
            title: title,
            subtitle: subtitle,
            imageUrl: imageUrl,
            address: address,
            contactEmail: email,
            description: description,
        };

        meetups = [newMeetup, ...meetups];
    }
</script>

<Header />
<main>
    <form on:submit|preventDefault={addMeetup}>
        <TextInput 
            id='title'
            label='Title'
            type='text'
            value={title}
            on:input = {event => (title = event.target.value)} />
        <TextInput 
            id='subtitle'
            label='Subtitle'
            type='text'
            value={subtitle}
            on:input = {event => (subtitle = event.target.value)} />
        <TextInput 
            id='address'
            label='Address'
            type='text'
            value={address}
            on:input = {event => (address = event.target.value)} />
        <TextInput 
            id='imageUrl'
            label='ImageUrl'
            type='text'
            value={imageUrl}
            on:input = {event => (imageUrl = event.target.value)} />
        <TextInput 
            id='email'
            label='Email'
            type='email'
            value={email}
            on:input = {event => (email = event.target.value)} />
        <TextInput 
            id='description'
            label='Description'
            type='description'
            controlType='textarea'
            rows='3'
            value={description}
            on:input = {event => (description = event.target.value)} />
        
        <Button type='submit' caption='add meetup'/>
    </form>
    <MeetupGrid {meetups} />
</main>

<style>
    main {
        margin-top: 5rem;
    }
    form {
        width: 30rem;
        max-width: 90%;
        margin: auto;
    }
</style>